mapper.select:
select
    count(*)
from
    academy_tb
where
    1 = 1

        and ATPT_OFCDC_SC_CODE = #{atptOfcdcScCode}


        and ADMST_ZONE_NM = #{admstZoneNm}


        and ACA_ASNUM = #{acaAsnum}


        and ACA_NM LIKE CONCAT('%', #{acaNm}, '%')


        and REALM_SC_NM = #{realmScNm}


        and LE_ORD_NM = #{leOrdNm}


        and LE_CRSE_NM = #{leCrseNm}

SELECT ACADEMY_ID, ATPT_OFCDC_SC_CODE, ATPT_OFCDC_SC_NM, ADMST_ZONE_NM, ACA_INSTI_SC_NM, ACA_ASNUM, ACA_NM, ESTBL_YMD, REG_YMD, REG_STTUS_NM, CAA_BEGIN_YMD, CAA_END_YMD, TOFOR_SMTOT, DTM_RCPTN_ABLTY_NMPR_SMTOT, REALM_SC_NM, LE_ORD_NM, LE_CRSE_LIST_NM, LE_CRSE_NM, PSNBY_THCC_CNTNT, THCC_OTHBC_YN, BRHS_ACA_YN, FA_RDNMA, FA_RDNDA, FA_RDNZC, FA_TELNO, LOAD_DTM FROM academy_tb where 1 = 1
and ATPT_OFCDC_SC_CODE = #{atptOfcdcScCode}
and ADMST_ZONE_NM = #{admstZoneNm}
and ACA_ASNUM = #{acaAsnum}
and ACA_NM LIKE CONCAT('%', #{acaNm}, '%')
and REALM_SC_NM = #{realmScNm}
and LE_ORD_NM = #{leOrdNm}
and LE_CRSE_NM = #{leCrseNm}
limit #{index}, #{pSize}
