mapper.delete:
delete
from
    like_tb
where
    user_id = #{userId}
    and ACADEMY_ID = #{academyId}

mapper.select:
select
    (select
        if(count(*) > 0, 1, 0)
    from
        user_tb
    where
        email = #{email}
        and user_id != #{userId}
    )
    + (select
        if(count(*) > 0, 2, 0)
    from
        user_tb
    where
        nickname = #{nickname}
        and user_id != #{userId}
    ) as result
